import { createSlice } from '@reduxjs/toolkit';
import data from "../../data/data.json"

// export const restaurantsSlice = createSlice({
//   name: 'restaurants',
//   initialState: {
//     initialValue: data.restaurants,
//     value: data.restaurants
//   },
//   reducers: {
//     filter : (state,action) => {state.value = state.initialValue.filter((e) => { e.rating < action.payload })},

//   }
// })

// export const { filter } = restaurantsSlice.actions

// export default restaurantsSlice.reducer

export const restaurantsSlice = createSlice({
    name: 'restaurants',
    initialState: {
      initialValue: data.restaurants,
      value: data.restaurants
    },
    reducers: {
      filter: (state, action) => {
        state.value = state.initialValue.filter((e) => e.rating > action.payload);
      },
    },
  })
  
  export const { filter } = restaurantsSlice.actions
  
  export default restaurantsSlice.reducer
