import { createSlice } from "@reduxjs/toolkit";
import data from "../../data/data.json";

export const dishesSlice = createSlice({
  name: "dishes",
  initialState: {
    initialValue: data.dishes,
    value: data.dishes,
  },
  reducers: {
    filterDishes: (state, action) => {
      switch (action.payload) {
        case "breakfast":
          state.value = state.initialValue.filter((dish) => dish.dishTiming.breakfast);
          break;
        case "lunch":
          state.value = state.initialValue.filter((dish) => dish.dishTiming.lunch);
          break;
        case "dinner":
          state.value = state.initialValue.filter((dish) => dish.dishTiming.dinner);
          break;
        case "":
          state.value = state.initialValue
          break;
      }
    }
  },
});


export const { filterDishes } = dishesSlice.actions;
export default dishesSlice.reducer;
