import styled from "styled-components";

interface IBold {
  bold?: boolean;
}

export const Clean_button = styled.button<IBold>`
  all: unset;
  font-size: 18px;
  font-weight: ${(props) => (props.bold == true ? "bold" : "normal")};
`;

export const AdvancedFilterButton = styled.button`
  all: unset;
  font-size: 18px;
  height: 100%;
  padding: 0px 10px;
  &:hover{
    background-color: #D0CFCF;
  }
`

export const DishTimeSortingButton = styled.button`
  all: unset;
  font-size: 18px;
  &.notActive&:before {
  content: "";
  position: absolute;
  bottom: -2px;
  left: 0;
  right: 0;
  height: 1.8px;
  background-color: #de9000;
  transform: scaleX(0);
  transform-origin: center;
  transition: transform 0.17s ease-in-out;
  }
  &.notActive&:hover&:before {
  transform: scaleX(1);
}
&.underline {
  border-bottom: #de9000 2.8px solid;
  cursor: pointer;
  text-decoration: none;
  color: black;
}
&.notActive {
  all: unset;
  cursor: pointer;
}
`

export const AddToBagButton = styled.button`
  all: unset;
  font-size: 18px;
  color: white;
  background-color: black;
  padding: 14px 35px;
  font-family: "HelveticaNeue-Light";
  letter-spacing: 1.5px;
`;
