import './App.css';
import { BrowserRouter, Routes, Route } from "react-router-dom";

import Homepage from './components/homepage/Homepage';
import Restaurants from './components/restaurants/Restaurants';
import Chefs from './components/chefs/Chefs';
import SingleRestaurant from './components/restaurants/restaurants_components/SingleRestaurant';
import { useEffect } from 'react';

function App() {
  useEffect(() => {
    function resetScrollPosition() {
      window.scrollTo(0, 0);
    }

    window.addEventListener('load', resetScrollPosition);
    window.addEventListener('hashchange', resetScrollPosition);

    return () => {
      window.removeEventListener('load', resetScrollPosition);
      window.removeEventListener('hashchange', resetScrollPosition);
    };
  }, []);

  return (
    <>
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<Homepage />} />
          <Route path="/restaurants" element={<Restaurants />} />
          <Route path="/restaurants/:single" element={<SingleRestaurant />} />
          <Route path="/chefs" element={<Chefs />} />
        </Routes>
      </BrowserRouter>
    </>
  );
}

export default App;
