import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";
import { IChef } from "../../typs/interfaces/slicersInterfaces";

// import data from "../../data/data.json";
let chefs_data: IChef[] = [];

const fetchChefsData = () => {
  const response = fetch("http://localhost:8000/api/chefs")
  .then((response) => {
    return response.json()
  })
  .catch((err) => console.log(err));
  return response;
}

chefs_data = await fetchChefsData()


const chefsSlice = createSlice({
  name: "chefs",
  initialState: {
    initialValue: chefs_data,
    value: chefs_data,
    chef_of_the_week_id: 5,
  },
  reducers: {},
});

export default chefsSlice.reducer;
