import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";
import { IDish } from "../../typs/interfaces/slicersInterfaces";
import data from "../../data/data.json"
// export const fetchDishes: any = createAsyncThunk(
//   "chefs/fetchChefs",
//   async () => {
//     const response = await axios.get("http://localhost:8000/api/dishes/");
//     return response.data;
//   }
// );

export const dishesSlice = createSlice({
  name: "dishes",
  initialState: {
    initialValue: data.dishes,
    value: data.dishes,
    current_dishes: data.dishes,
  },
  reducers: {
filterDishes: (state, action) => {
  // state.value = state.initialValue.filter((dish: IDish) => action.payload.dishes_id_list.includes(dish.id));
  state.value = state.initialValue.filter((dish) => action.payload.dishes_id_list.includes(dish.id));

  switch (action.payload.dish_timing) {
    case "breakfast":
      // state.value = state.value.filter((dish: IDish) => dish.dishTiming.breakfast)
      state.value = state.value.filter((dish) => dish.dishTiming.breakfast)
      break;
    case "lunch":
      // state.value = state.value.filter((dish: IDish) => dish.dishTiming.lunch)
      state.value = state.value.filter((dish) => dish.dishTiming.lunch)
      break;
    case "dinner":
      // state.value = state.value.filter((dish: IDish) => dish.dishTiming.dinner)
      state.value = state.value.filter((dish) => dish.dishTiming.dinner)
      break;
    case "":
      break;
  }
},
  },
  // extraReducers: (builder) => {
  //   builder
