import { createSlice } from "@reduxjs/toolkit";
import { stat } from "fs";
import data from "../../data/data.json";
import { IDish } from "../../typs/interfaces/slicersInterfaces";

export const dishesSlice = createSlice({
  name: "dishes",
  initialState: {
    initialValue: data.dishes,
    value: data.dishes,
    current_dishes: data.dishes
  },
  reducers: {

    SetDishesByIds: (state, action) => {
      const current_dishes = action.payload.map((dish_id:number) => {
        state.initialValue.filter((dish) => Number(dish.id) == Number(dish_id))
      })
      console.log(current_dishes)
    }

    ,filterDishes: (state, action) => {
      let dishes_list:IDish[];
      switch (action.payload.dish_timing) {
        case "breakfast":
          // state.value = state.initialValue.filter((dish) => dish.dishTiming.breakfast);
          // action.payload.dishes_id_list.filter((dish_id:number) => {
          //  state.initialValue[dish_id]
          // })
          break;
        case "lunch":
          state.value = state.initialValue.filter((dish) => dish.dishTiming.lunch);
          break;
        case "dinner":
          state.value = state.initialValue.filter((dish) => dish.dishTiming.dinner);
          break;
        case "":
          state.value = state.initialValue
          break;
      }
    }
  },
});


export const { filterDishes, SetDishesByIds } = dishesSlice.actions;
export default dishesSlice.reducer;
