import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";
import { IChef } from "../../typs/interfaces/slicersInterfaces";

// let fetch_data = await axios.get("http://localhost:8000/api/chefs/")
// let chefs_data: IChef[] = fetch_data.data

const fetchChefs = createAsyncThunk("chefs/fetchChefs", async () => {
  const response = await axios.get("http://localhost:8000/api/chefs/");
  return response.data;
});

fetchChefs()

let chefs_data: [] = []

const a = fetch("http://localhost:8000/api/chefs/").then(response => response.json()).then( data => {return data})

const chefsSlice = createSlice({
  name: "chefs",
  initialState: {
    initialValue: chefs_data,
    value: chefs_data,
    chef_of_the_week_id: 5,
  },
  reducers: {},
  extraReducers: (builder) => {
    builder
    .addCase(fetchChefs.fulfilled, (state, action) => {
      state.value = action.payload.result;
      state.initialValue = action.payload.result;
      console.log("WORKED")
    })
      .addCase(fetchChefs.pending, (state, action) => {
        // state.isLoading = true;
        console.log("pending")
      })
      .addCase(fetchChefs.rejected, (state, action) => {
        console.log("rejected")
        // state.isLoading = false;
        //console.log(action.error.message);
      });
  }
});

export default chefsSlice.reducer;
