import React from "react";
import ICard from "../../../../typs/interfaces/ICard";
import "./Card.css";


const Card: React.FC<ICard> = (props) => {
      return (<div className={`card ${props.class}`}>
        {props.img_rest ? <img className="img_rest" src={props.img_rest} /> : <img className="img_dish" src={props.img_dish} /> }
        <div className="card_bottom_div">
          <h3>{props.name}</h3>
          <section>{props.icons ? props.icons?.map((icon:string) => <img className="icon" src={icon} />): null }</section>
          <h2>{props.chefName ? props.chefName : props.dishDescription}</h2>
          <section>{props.rating ? <img className="rating" src={"/assets/icons/rating_svgs/rate-" + props.rating + ".svg"} /> : null }</section>
          {props.price ? <div><hr /><p>â‚ª{props.price}</p><hr /></div> : null}
        </div>
      </div>
      )
    }

export default Card;
