import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";
import { IChef } from "../../typs/interfaces/slicersInterfaces";

let chefs_data: IChef[] = [];

(async () => {
  try {
    const response = await axios.get("http://localhost:8000/api/chefs/");
    chefs_data = response.data;
    console.log(chefs_data)
  } catch (error) {
    console.error(error);
  }
})();

const chefsSlice = createSlice({
  name: "chefs",
  initialState: {
    initialValue: [],
    value: chefs_data,
    chef_of_the_week_id: 5,
  },
  reducers: {},
});

export default chefsSlice.reducer;
