import React, { useEffect, useState } from "react";
import { White_button } from "../../typs/buttons/Styled_buttons";
import Card from "../homepage/hp-components/card/Card";
import Footer from "../homepage/hp-components/footer/Footer";
import Navbar from "../navbar/Navbar";
import data from "../../data/data.json";
import { getChefNameByRestId } from "../../store/slicers/chefsSlicer";

import "./restaurants.css";
import { useDispatch, useSelector } from "react-redux";
import { filter } from "../../store/slicers/restaurantsSlicer";
import { NavLink } from "react-router-dom";


interface IJsonRest {
  id: number, 
  is_favorite: boolean , 
  name: string,
  img_url: string,
  hours: Array<number>, 
  address: string, 
  rating: number, 
  dish_ids: Array<number>, 
  opening_year: number
}


const Restaurants: React.FC = () => {
  const [boldName, setBoldName] = useState("all");
  const dispatch = useDispatch();
  const restaurants = useSelector((state: any) => state.restaurants.value);
  useEffect(() => {
    dispatch(filter(boldName));
  }, [boldName]);

  
  let rest_grid_div
  let chefNames:Array<string>= []
   // useEffect(()=>{

  restaurants.forEach((restaurant:IJsonRest) => {
    dispatch(getChefNameByRestId(restaurant.id));
    chefNames[restaurant.id-1] = useSelector((state:any) => state.chefs.chef_name);
  });

    
    rest_grid_div = restaurants.map((restaurant: any, index: number) => {
      
      // dispatch(getChefNameByRestId(restaurant.id))
      const chefName = chefNames[index]

  return (  <>
    <NavLink to={`/restaurants/${restaurant.id}`}>
    <Card class="rest"
      img={restaurant.img_url}
      name={restaurant.name}
      chefName={
        
        chefName
      }
      rating={restaurant.rating}
    /></NavLink></>)
    })

  // },[chefName])
  


  
  return (
    <>
      <Navbar />

      <div className="filters_first_row">
        <div className="buttons_first_row">
          <White_button
            name="all"
            bold={boldName === "all"}
            onClick={() => {
              setBoldName("all");
              dispatch(filter(boldName));
            }}
          >
            All
          </White_button>
          <White_button
            name="new"
            bold={boldName === "new"}
            onClick={() => {
              setBoldName("new");
              dispatch(filter(boldName));
            }}
          >
            New
          </White_button>
          <White_button
            name="most_popular"
            bold={boldName === "most_popular"}
            onClick={() => {
              setBoldName("most_popular");
              dispatch(filter(boldName));
            }}
          >
            Most Popular
          </White_button>
          <White_button
            name="open_now"
            bold={boldName === "open_now"}
            onClick={() => {
              setBoldName("open_now");
              dispatch(filter(boldName));
            }}
          >
            Open Now
          </White_button>
          <White_button
            name="map_view"
            bold={boldName === "map_view"}
            onClick={() => setBoldName("map_view")}
          >
            Map View
          </White_button>
        </div>
      </div>

      <div className="filters_second_row">
        <div className="buttons_second_row">
          <White_button>
            Price Range{" "}
            <img src="assets/icons/rest_page_icons/arrow_down.svg" />
          </White_button>
          <White_button>
            Distance <img src="assets/icons/rest_page_icons/arrow_down.svg" />
          </White_button>
          <White_button>
            Rating <img src="assets/icons/rest_page_icons/arrow_down.svg" />
          </White_button>
        </div>
      </div>

      <div className="restaurants_grid">
            {rest_grid_div}
      </div>

      <Footer />
    </>
  );
};

export default Restaurants;
